sysauto_script.script:
//Get today's time and date
var rightNow = new GlideDateTime();

//Query the database for NeedIt Task Records with due date fields values older than current time only return NeedIt Task Records that do no have a state value of closed complete

//Create the variable for the record of the needIt task to use in query
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

//Query the GlideRecord for due date, with the conditions of less than or equal to right now
overdueNITasks.addQuery('due_date', '<=', rightNow);

//Query the GlideRecord for the state, with the condidtions of State does not equal closed complete (3)
overdueNITasks.addQuery('state','!=', 3);

//Run the query
overdueNITasks.query();
//Write a log message for each overdue NeedIt Task record

while(overdueNITasks.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask',overdueNITasks,overdueNITasks.number,gs.getUserName());
}
